# method for adding custom label
customLabel <- function(x){
    vec<-NULL
    for (X in x){
        if(X%%2!=0)
            vec<-c(vec,"")
        else
            vec<-c(vec,as.integer(X))
        vec
    }
      as.vector(vec)      
}

